$(document).ready(function(){$("#actionToShowUserDetails").click(function(){var d=$("#showUserDetails");var c=d.css("display");if(c=="none"){d.show();$("#actionToShowUserDetails").html('<span class="glyphicon glyphicon-user"></span>Hide User Details')}else{d.hide();$("#actionToShowUserDetails").html('<span class="glyphicon glyphicon-user"></span>Show User Details')}});var a=localStorage.getItem("stateButtonShow");var b=localStorage.getItem("machineId");if(a=="showButtons"){console.log("= 'showButtons'");showButtons(b)}else{if(a=="hideButtons"){console.log("= 'hideButtons'");hideButtons(b)}else{console.log("= null")}}});function showButtons(a){$("#updateButton"+a).hide();$("#listButtons"+a).show();localStorage.setItem("stateButtonShow","showButtons");localStorage.setItem("machineId",a)}function hideButtons(a){$("#lock"+a).parent().hide();$("#updateButton"+a).show();localStorage.setItem("stateButtonShow","hideButtons");localStorage.setItem("machineId",a)};